import{Command as e}from"commander";import{CompressCommand as o}from"./CompressCommand.js";import{DecompressCommand as m}from"./DecompressCommand.js";export function makeProgram(){let r=new e;return r.command("compress").description("compress to Yaz0 file").argument("<input>","file to be compressed").argument("[output]","output file").option("-l, --level <number>","compression level (0..9)",Number,9).option("-s, --search-range <number>","defines a custom search range (0..4095)",Number).option("-w, --width <number>","minimum output width",Number,0).option("-f, --fast","disables slower compression methods",!1).action(o),r.command("decompress").description("decompress from Yaz0 file").argument("<input>","Yaz0 file to be decompressed").argument("[output]","output file").action(m),r}